import React, { useState } from 'react'
import '../assets/styles/Counter.css'


const Counter = () => {
    const [counter, setCounter] = useState(0);

    return (
        <section class="Counter">
            <h1> {counter}</h1>
            <button onClick={() => setCounter(counter + 1)} ></button>
            <button onClick={() => setCounter(counter - 1)} ></button>
            <button onClick={() => setCounter(0)} ></button>
            <div class="elements visor">Contador</div>
        <button class="elements plus">+</button>
        <button class="elements minus">–</button>
        <button class="elements plusTwo">+2</button>
        <button class="elements minusTwo">-2</button>
        <button class="elements reset">Reset</button>
        </section>
    )
}

export default Counter;
