import React, { useState } from 'react'


const Counter = () => {
    const [counter, setCounter] = useState(0);

    return (
        <section>
            <h1> {value={counter}}</h1>
            <button mudaValor={() => setCounter(counter + 1)} ></button>
            <button mudaValor={() => setCounter(counter - 1)} ></button>
            <button mudaValor={() => setCounter(counter + 1)} ></button>
        </section>
    )
}

export default Counter;
