import React, { Component } from 'react'
import { Button } from './Button'
import MessageError from './MessageError'
import Visor from './VIsor'



export class Counter extends Component {
    state = {
        number: 0,
        classMessageError: "",
    }

    increment = () => {
        this.setState({ classMessageError: "" })
        this.setState({ number: this.state.number + 1 })
    }
    decrement = () => {
        this.setState({ number: this.state.number - 1 })
        if (this.state.number <= 0) {
            this.setState({ number: 0 })
            this.setState({ classMessageError: "teste" })
        } else {
            this.setState({ classMessageError: "" })
        }
    }
    reset = () => {
        this.setState({ classMessageError: "" })
        this.setState({ number: 0 })
    }
    render() {
        return (
            <section className="myCounter">
                <MessageError value={this.state.classMessageError} />
                <Visor value={this.state.number} />
                <Button valueOfButton="+" executeFunction={() => this.increment()} />
                <Button valueOfButton="-" executeFunction={() => this.decrement()} />
                <Button valueOfButton="Reset" executeFunction={() => this.reset()} />
            </section>
        )
    }
}
