import React, { useState } from 'react'
import '../assets/styles/Reset.css'


const Counter = () => {
    const [counter, setCounter] = useState(0);
    if (counter < 0) {
        setCounter(0)
    }
    return (
        <section className="myCounter">
            <div className="elements visor">{counter}</div>
            <button className="elements plus" onClick={() => setCounter(counter + 1)} >+</button>
            <button className="elements minus" onClick={() => setCounter(counter - 1)} >-</button>
            <button className="elements reset" onClick={() => setCounter(0)} >Reset</button>
            <button className="elements plustTwo" onClick={() => setCounter(counter + 2)} >+2</button>
            <button className="elements minusTwo" onClick={() => setCounter(counter - 2)} >-2</button>

        </section>
    )
}

export default Counter;
